mapper.select:
	select B.*
	from
	(
		select rownum RNUM, A.*
		from(
			select * from memberview
			
				where mem_name||dept_name||pos_name like '%'||#{keyword}||'%'
			
			order by pos_no, mem_no
		)A
where rownum <= #{firstRecordIndex}+#{recordCountPerPage}
	)B
	where B.RNUM > #{firstRecordIndex}
