mapper.select:
select B.* from ( select rownum RNUM, A.* from ( select MB.MEM_ID, MB.MEM_NAME,M.*,MR.MSGREV_NO, MR.MEM_NO rev_Mem, MR.MSGREV_DEL, MR.MSGREV_DATE from message M join msgrev MR on M.msg_no = MR.MSG_NO join member MB on M.MEM_NO = MB.MEM_NO and MR.mem_no = #{memNo} and msgRev_del = 'N'
and MB.MEM_ID || msg_title || msg_content like '%' || #{keyword} || '%'
order by msgrev_no desc )A where rownum <= #{firstRecordIndex} + #{recordCountPerPage} )B where RNUM > #{firstRecordIndex}

select B.* from ( select rownum RNUM, A.* from ( select MB.MEM_ID, MB.MEM_NAME, M.*,MR.MSGREV_NO, MR.MEM_NO rev_Mem, MR.MSGREV_DEL, MR.MSGREV_DATE from message M join msgrev MR on M.msg_no = MR.MSG_NO join member MB on MR.MEM_NO = MB.MEM_NO and M.MEM_NO = #{memNo} and msg_del = 'N'
and MB.MEM_ID || msg_title || msg_content like '%' || #{keyword} || '%'
order by M.msg_no desc )A where rownum <= #{firstRecordIndex} + #{recordCountPerPage} )B where RNUM > #{firstRecordIndex}

mapper.update:
update msgrev
set msgRev_date = sysdate
where msgrev_no = #{msgrevNo}

mapper.select:
		select max(msgrev_no) from messageView
		where rev_mem = #{memNo}
		and msg_del = 'N'
		
		and mem_id || msg_title || msg_content like '%' || #{keyword} || '%'
		
and msgrev_no < #{msgrevNo}

		select count(*) from messageView
		where rev_mem = #{memNo}
		and msg_del = 'N'
		
		and mem_id || msg_title || msg_content like '%' || #{keyword} || '%'
		
and msgrev_no < #{msgrevNo}

