mapper.select:
select W.*, M.mem_id from webhardfile W
join member M
on W.mem_no = M.mem_no
where web_no = #{webNo}

	and W.mem_no = #{memNo}

order by file_No desc

mapper.update:
update webhardfile
set file_downcount = file_downcount + 1
where file_no = #{fileNo}

mapper.delete:
delete from webhardfile
where file_no = #{fileNo}

	and mem_no = #{memNo}

mapper.update:
update webhard
set web_category = #{webCategory}
where web_no = #{webNo}
